import React from 'react';

import './homePage.css';

import Header from '../../components/header/header';
import Navbar from '../../components/navbar/navbar';
import SquareLink from '../../components/squareLink/squareLink';
import Footer from '../../components/footer/footer';

const HomePage = () => {

    let currentYear = new Date().getFullYear();

    return (
        <>
        <Header />
        <Navbar />
        <main>

            <SquareLink to={"/videogames/ ?historicalLow=false"} color={"#A79AF5"} text="all games">
                <span className="material-symbols-outlined" id='icon-all'>content_copy</span>
            </SquareLink>

            <SquareLink to={"/videogames/ ?historicalLow=false&minDiscount=0&maxDiscount=100"} color={"#A4FB95"} text="all discount" >
                <span className="material-symbols-outlined" id='icon-percent'>percent</span>
            </SquareLink>

            <SquareLink to={"/videogames/ ?historicalLow=false&maxPrice=0"} color={"#FF9B63"} text="free games" >
            <span class="material-symbols-outlined" id='icon-free'>redeem</span>
            </SquareLink>

            <SquareLink to={"/videogames/ ?historicalLow=true"} color={"#FFE0E0"} text="historical low" >
                <span class="material-symbols-outlined" id='icon-low' >trending_down</span>
            </SquareLink>

            <SquareLink to={"/videogames/ ?historicalLow=false&minDiscount=50"} color={"#FF5454"} text="huge discount" />
            <SquareLink to={"/videogames/ ?historicalLow=false&minYear=" + currentYear} color={"#C6FFFC"} text="last releases" />
            <SquareLink to={"/videogames/ ?historicalLow=false&systems=Windows"} color={"#FDFF9B"} text="windows" />
            <SquareLink to={"/videogames/ ?historicalLow=false&systems=MacOS"} color={"#FD92FF"} text="mac" />
        </main>
        <Footer />
        </>
    )
}

export default HomePage;
