import { useState } from "react";
import { Link } from "react-router-dom";
import UserForm from "../../components/userForm/userForm";


const SignUpPage = ({user = false, setUser = () => {}}) => {

    const [userEmail, setUserEmail] = useState("");
    const [userPassword, setUserPassword] = useState("");

    const [isModelNoDisplayed, setIsModelNoDisplayed] = useState("none")
    const [isModelYesDisplayed, setIsModelYesDisplayed] = useState("none")

    const emailOnChange = e => setUserEmail(e.target.value);

    const passwordOnchange = e => setUserPassword(e.target.value)

    const addUserOnClick = () => {
        fetch("http://localhost:8081/user", {
            method: 'POST',
            body: JSON.stringify({
                email: userEmail,
                password: userPassword
            }),
            mode: 'cors',
            headers: {
            'Access-Control-Allow-Origin':'*',
            'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(responseJSON => {
            if (responseJSON) {
                setUser(true);
                setIsModelYesDisplayed("block");
            }
            else {
                setIsModelNoDisplayed("block");
                setTimeout(() => setIsModelNoDisplayed("none"), 2000)
            }
        })
    }

    // window.addEventListener("popstate", () => {
    //     if (user) {
    //         let response = window.confirm("are you sure you want to log out?");
    //         if (!response) console.log("no");
    //     }
    // })

    return (
        <div>
            <div className='container-title'>
                    <span className="material-symbols-outlined logo">bar_chart</span>
                    <h1 className='title'>VIDEOGAMES PRICE TRACKER</h1>
            </div>
            <UserForm text="Sign Up" userEmail={userEmail} emailOnChange={emailOnChange} userPassword={userPassword} passwordOnchange={passwordOnchange} addUserOnClick={addUserOnClick}/>
            <div className="userPage-modal" id="userPage-modal-no" style={{display: isModelNoDisplayed}}>
                <p>email already exists</p>
            </div>
            <div className="userPage-modal" id="userPage-modal-yes" style={{display: isModelYesDisplayed}}>
                <div>
                    <p>welcome new user</p>
                    <Link to="/">enter</Link>
                </div>
            </div>
        </div>
    )
}

export default SignUpPage;
