import React, { useEffect, useState } from 'react';

const DropdownBoxCheckbox = ({isDropdownBoxDisplayed = "none", leftSpaceFromWindow, text = "", addOrRemoveField = () => {}}) => {

    const [list, setList] = useState([])

    useEffect(
        () => {
            fetch("http://localhost:8081/" + text)
            .then(response => response.json())
            .then(responseJSON => setList(responseJSON))
        }, []
    )
    
    return (
        <div style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}} className="dropdown-box-checkbox" >
            <div className='dropdown-box-checkbox-inner'>
                {list.map(item => <CheckboxInput key={item} item={item} addOrRemoveField={addOrRemoveField} text={text}/> )}
            </div>
        </div>
    )
}

export default DropdownBoxCheckbox;


const CheckboxInput = ({addOrRemoveField = () => {} , item = "", text = ""}) => {

    const [isChecked, setIsChecked] = useState(false)

    const onChange = e => {
        setIsChecked(e.target.checked);
        addOrRemoveField(text, item)
    }

    return (
        <div className="dropdown-box-checkbox-item">
            <input type="checkbox" checked={isChecked} onChange={onChange}/>
            <p>{item}</p>
        </div>
    )

}
