import React, { useEffect, useState } from 'react';

const DropdownBoxCheckbox = ({isDropdownBoxDisplayed = "none", leftSpaceFromWindow, text = "", addField = () => {}}) => {

    const [list, setList] = useState([])

    useEffect(
        () => {
            fetch("http://localhost:8081/" + text)
            .then(response => response.json())
            .then(responseJSON => setList(responseJSON))
        }, []
    )
    
    return (
        <div style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}} className="dropdown-box-checkbox" >
            <div className='dropdown-box-checkbox-inner'>
                {list.map(item => <div key={item} className="dropdown-box-checkbox-item">
                    <input type="checkbox" checked={false} onChange={addField}/>
                    <p>{item}</p>
                </div> )}
            </div>
        </div>
    )
}

export default DropdownBoxCheckbox;


const CheckboxInput = ({addField = () => {} }) => {

    const [isChecked, setIsChecked] = useState(false)

    const [value, setValue] = useState();

    const onChange = e => {
        setIsChecked(e.target.checked);
        addField()
    }

    return (
        <input type="checkbox" onChange={onChange} checked={isChecked}/>
    )

}
