import React, { useState } from 'react';
import { useSearchParams } from "react-router-dom";
import { Routes, Route, } from "react-router-dom";
import './gamesPage.css';

import Navbar from '../../components/navbar/navbar';
import Footer from '../../components/footer/footer';
import FilteringAndSorting from '../../components/filteringAndSorting/filteringAndSorting';
import Main from '../../components/main/main';

const GamesPage = ({user = false, setUser = () => {}}) => {

    const [searchParams, setSearchParams] = useSearchParams();
    const [videogames, setVideogames] = useState([]);
    const [currentSorting, setCurrentSorting] = useState([false, false, false, false, false, false]);

    const onChangeSorting = event => {
        let sorting = event.target.value;
        let videogamesCopy = [...videogames]
        if (sorting === "A-Z") videogamesCopy.sort((a, b) => a.title.toUpperCase().localeCompare(b.title.toUpperCase()));
        else if (sorting === "Z-A") videogamesCopy.sort((a, b) => b.title.toUpperCase().localeCompare(a.title.toUpperCase()));
        else if (sorting === "asc") videogamesCopy.sort((a, b) => priceSorting(a, b));
        else if (sorting === "desc") videogamesCopy.sort((a, b) => b.basePrice - a.basePrice);
        else if (sorting === "oldest") videogamesCopy.sort((a, b) => Number(a.releaseDate.split("-").join("")) - Number(b.releaseDate.split("-").join("")));
        else if (sorting === "newest") videogamesCopy.sort((a, b) => Number(b.releaseDate.split("-").join("")) - Number(a.releaseDate.split("-").join("")));
        setVideogames(videogamesCopy)
    }

    const priceSorting = (a, b) => {
        if (a.currentDiscountedPrice && b.currentDiscountedPrice) return a.currentDiscountedPrice - b.currentDiscountedPrice;
        else if (a.currentDiscountedPrice) return a.currentDiscountedPrice - b.basePrice;
        else if (b.currentDiscountedPrice) return a.basePrice - b.currentDiscountedPrice;
        else return a.basePrice - b.basePrice;
    }

    return (
        <>
        <Navbar user={user} setUser={setUser} />
        <FilteringAndSorting onChangeSorting={onChangeSorting} setSearchParams={setSearchParams} searchParams={searchParams} currentSorting={currentSorting} setCurrentSorting={setCurrentSorting} /*newSearch={newSearch}*//>
        <main>
            <Routes>
                <Route index element={<Main setVideogames={setVideogames} videogames={videogames} searchParams={searchParams} currentSorting={currentSorting} />} />
                <Route path=":query" element={<Main setVideogames={setVideogames} videogames={videogames} searchParams={searchParams} currentSorting={currentSorting}/>}/>
            </Routes>
        </main>
        <Footer />
        </>
    )
}

export default GamesPage;
