import { useParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';


const Main = () => {

    let params = useParams();

    const [videogames, setVideogames] = useState([]);

    const getVideogames = () => {
        fetch("http://localhost:8081/videogame/" + params.query)
        .then(response => response.json())
        .then(responseJSON => setVideogames(responseJSON));
    }

    useEffect(
        getVideogames,[]
    )

    return (
        <div>
        {videogames.forEach(v => <p>{v.title}</p>)}
        </div>
    )
}

export default Main;
