import React, { useEffect, useState } from 'react';

import './filteringAndSorting.css';

const FilteringAndSorting = ({onChangeSorting = {}}) => {

    //window.location.href = "http://localhost:8080/videogame/33"

    // const horizontalScroll = e => {
    //     const delta = Math.max(-1, Math.min(1, (e.nativeEvent.wheelDelta || -e.nativeEvent.detail)))
    //     e.currentTarget.scrollLeft -= (delta * 100)
    // }

    return (
        <div id='filter-sorting-container' /*onWheel={horizontalScroll}*/>

            <div id='filter-sorting-bar'>

                    <FilterItem text="types" />
                    <FilterItem text="categories" />
                    <FilterItem text="price" />
                    <FilterItem text="discount" />
                    <FilterItem text="systems" />
                    <FilterItem text="release year" />

                    {/* historical low don't need a dropdown */}
                    <div className='filter-sorting-item' style={{whiteSpace: "nowrap"}}>
                        historical low
                   </div>

                   <Sorting onChangeSorting={onChangeSorting}/>

            </div>

        </div>
    )

}

const FilterItem = ({text = ""}) => {

    let dropdown;

    const [isDropdownBoxDisplayed, setIsDropdownBoxDisplayed] = useState("none");

    const [leftSpaceFromWindow, setLeftSpaceFromWindow] = useState(0);

    if (text === "types" || text === "categories" || text == "systems")
        dropdown = <DropdownBoxCheckbox isDropdownBoxDisplayed={isDropdownBoxDisplayed} leftSpaceFromWindow={leftSpaceFromWindow} text={text}/>
    else dropdown = <DropDownBoxRange isDropdownBoxDisplayed={isDropdownBoxDisplayed} leftSpaceFromWindow={leftSpaceFromWindow} text={text}/>


    const changeDisplayOnClick = e => {
        if (isDropdownBoxDisplayed === "none") {
            setIsDropdownBoxDisplayed("block");
            if (e.target == e.currentTarget) setLeftSpaceFromWindow(e.target.getBoundingClientRect().left);
            else setLeftSpaceFromWindow(e.currentTarget.getBoundingClientRect().left)
        }
        else { // stop the propagation when click on checkbox or number input
            if (e.target == e.currentTarget) setIsDropdownBoxDisplayed("none")
        }
    }

    return (
        <div className='filter-sorting-item' onClick={changeDisplayOnClick} onMouseLeave={() => setIsDropdownBoxDisplayed("none")}>
            {text}
            {dropdown}
        </div>
    )

}

const DropdownBoxCheckbox = ({isDropdownBoxDisplayed = "none", leftSpaceFromWindow, text = ""}) => {

    const [list, setList] = useState([])

    useEffect(
        () => {
            fetch("http://localhost:8081/" + text)
            .then(response => response.json())
            .then(responseJSON => setList(responseJSON))
        }, []
    )
    
    return (
        <div style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}} className="dropdown-box-checkbox" >
            <div className='dropdown-box-checkbox-inner'>
                {list.map(item => <div key={item} className="dropdown-box-checkbox-item">
                    <input type="checkbox" value={item} />
                    <p>{item}</p>
                </div> )}
            </div>
        </div>
    )
}

const DropDownBoxRange = ({isDropdownBoxDisplayed = "none", leftSpaceFromWindow, text = ""}) => {


    let min = "0";
    let max;
    let minPlaceholder;
    let maxPlaceholder;

    if (text === "price") {
        max = "999";
        minPlaceholder = min + " €"
        maxPlaceholder = max + " €"
    }
    else if (text === "discount") {
        max = "100";
        minPlaceholder = min + "%"
        maxPlaceholder = max + "%"
    }
    else if (text === "release year") {
        min = "2000";
        max = new Date().getFullYear().toString();
        minPlaceholder = min;
        maxPlaceholder = max;
    }
    
    return (
        <div style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}} className="dropdown-box-range" >
            <div className="dropdown-box-range-inner">
                <p className='dropdown-box-range-text'>from</p>
                <input type="number" min={min} max={max} placeholder={minPlaceholder} value=""/>
                <p className='dropdown-box-range-text'>to</p>
                <input type="number" min={min} max={max} placeholder={maxPlaceholder} value=""/>
            </div>
        </div>
    )
}

const Sorting = ({onChangeSorting = () => {}}) => {

    let dropdown;

    const [isDropdownBoxDisplayed, setIsDropdownBoxDisplayed] = useState("none");

    const [leftSpaceFromWindow, setLeftSpaceFromWindow] = useState(0);

    const changeDisplayOnClick = e => {
        if (isDropdownBoxDisplayed === "none") {
            setIsDropdownBoxDisplayed("block");
            if (e.target == e.currentTarget) setLeftSpaceFromWindow(e.target.getBoundingClientRect().left);
            else setLeftSpaceFromWindow(e.currentTarget.getBoundingClientRect().left)
        }
        else { // stop the propagation when click on checkbox or number input
            if (e.target == e.currentTarget) setIsDropdownBoxDisplayed("none")
        }
    }

    let sortingList = [ // value - text
        ["A-Z", "A - Z"],
        ["Z-A", "Z - A"],
        ["asc", "PRICE ASC"],
        ["desc", "PRICE DESC"],
        ["oldest", "RELEASE DATE ↑"],
        ["newest", "RELEASE DATE ↓"],
    ]

    return (
        <div id='filter-sorting-item' onClick={changeDisplayOnClick} onMouseLeave={() => setIsDropdownBoxDisplayed("none")}>
            sorting
            <div id='dropdown-box-sorting' style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}}>
                <div id='dropdown-box-sorting-inner'>
                    {sortingList.map(el => <div key={el[0]} className="">
                        <input type="radio" name='sorting' value={el[0]} onChange={onChangeSorting}/>
                        <p>{el[1]}</p>
                    </div>)}
                </div>
            </div>
        </div>
    )
}


export default FilteringAndSorting;
