import { useParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';

import GameInlist from "./gameInList";


const Main = (props) => {

    let params = useParams().query;

    const [previousParams, setParams] = useState("");

    //const [videogames, setVideogames] = useState([]);

    if (params != previousParams) {
        setParams(params);
        getVideogames()
    }

    function getVideogames() {
        //if (params == undefined) params = "";
        fetch("http://localhost:8081/videogames/" + params)
        .then(response => response.json())
        .then(responseJSON => props.setVideogames(responseJSON));
    }

    return (
        <>
        {props.videogames.map(v => <GameInlist key={v.videogameId} game={v}/>)}
        </>
    )
}

export default Main;
