import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, } from "react-router-dom";
import './App.css';

import Header from './header';
import Navbar from './navbar';
import SquareLink from './squareLink';
import Footer from './footer';

const App = () => {

  const [img, setImg] = useState([{title: "ciao"}]);

  const cliccato = () => {
    console.log("inizio")
    fetch("http://localhost:8080/videogames")
    .then(response => response.json())
    .then(data => setImg(data))
  }

  const mainHomePage = <> {/* sono da mettere tutti i filter*/}
    <SquareLink to={"/"} color={"#A79AF5"} text="all games"/>
    <SquareLink to={"/"} color={"#A4FB95"} text="all discount" />
    <SquareLink to={"/"} color={"#FF9B63"} text="free games" />
    <SquareLink to={"/"} color={"#FFE0E0"} text="historical low" />
    <SquareLink to={"/"} color={"#FF5454"} text="huge discount" />
    <SquareLink to={"/"} color={"#C6FFFC"} text="last releases" />
    <SquareLink to={"/"} color={"#FDFF9B"} text="windows" />
    <SquareLink to={"/"} color={"#FD92FF"} text="mac" />
  </>

  return (
    <>
    <Routes>
      <Route path="/" element={<Header />} />
      <Route path="/games" element={<></>} />
      <Route path="*" element={<Header />} />
    </Routes>
    <Navbar />
    <Routes>
      <Route path="/games" element={<div>games</div>} />
    </Routes>
    <main>
    <Routes>
      <Route path="/" element={mainHomePage} />
      <Route path="/games" element={<div>games</div>} />
      <Route path="*" element={mainHomePage} />
    </Routes>
    </main>
    <Footer />
    <button onClick={cliccato}>carica immagine</button>

    {img.forEach(n => <div>{n.title}</div>)/*img.forEach(n => <img src={"data:image/png;base64," + n.cover} />) */}
    </>
  )
}

export default App;
