import { useParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';


const Main = () => {

    const [params, setParams] = useState(useParams().query)

    const [videogames, setVideogames] = useState([]);

    function getVideogames() {
        fetch("http://localhost:8081/videogame/" + params)
        .then(response => response.json())
        .then(responseJSON => setVideogames(responseJSON));
    }

    useEffect(
        getVideogames, [params]
    )


    return (
        <div>
        {videogames.map(v => <p key={v.videogameId}>{v.title}</p>)}
        {/* {useParams().query} */}
        </div>
    )
}

export default Main;
