import { useParams, useSearchParams } from "react-router-dom";
import React, { useEffect, useState } from 'react';

import GameInlist from "./gameInList";


const Main = (props) => {

    let params = useParams();
    console.log("query =" + params.query)

    const [previousUrl, setPreviousUrl] = useState("http://localhost:3000");

    if (window.location.href != previousUrl) {
        setPreviousUrl(window.location.href);
        getVideogames()
    }

    function getVideogames() {
        //if (params == undefined) params = "";
        console.log(props.searchParams.get("types"))
        let url = "http://localhost:8081/videogames/";
        if (props.searchParams.get("types") == null) url += params.query;
        else url += params.query + "?types=" + props.searchParams.get("categories");
        fetch(url)
        .then(response => response.json())
        .then(responseJSON => props.setVideogames(responseJSON)); // anche qui devo sortarli
                                                                //  in base a come è gia il sort
                                                                // oppure annullo il sort
    }

    return (
        <>
        {props.videogames.map(v => <GameInlist key={v.videogameId} game={v}/>)}
        </>
    )
}

export default Main;
