import React, { useState } from 'react';

import "./navbar.css"; 

import { Link } from "react-router-dom";

const Navbar = () => {

    const fixSearch = () => {
        if (window.innerWidth < 600) setIsSearchDisplayed("none");
        else if (window.innerWidth > 600) setIsSearchDisplayed("block");
    }

    const [searchValue, setSearchValue] = useState("");
    const [isSearchDisplayed, setIsSearchDisplayed] = useState("block")

    const onChangeSearchValue = event => {
        setSearchValue(event.target.value);
        if (event.target.value === "") return;
    }

    const onClickSearch = () => {
    }

    const [isMenuDisplayed, setIsMenuDisplayed] = useState("none");

    const changeDisplayMenu = () => {
        if (isMenuDisplayed === "none") showMenu();
        else hideMenu();
    }

    const showMenu = () => setIsMenuDisplayed("block");
    const hideMenu = () => setIsMenuDisplayed("none");

    const fixMenu = () => {
        if (window.innerWidth > 768) hideMenu();
    }

    window.addEventListener("resize", fixMenu);
    window.addEventListener("resize", fixSearch);

    return (
        <nav>
            <div>
                <Link to="/">
                    <span className="material-symbols-outlined home">home</span>
                </Link>
                <input type="text" className="search" placeholder='search...' value={searchValue} onChange={onChangeSearchValue} style={{"display": isSearchDisplayed}}></input>
                <button onClick={onClickSearch} id="btn-lens"><span className="material-symbols-outlined lens">search</span></button>
            </div>
            <div>
                <Link to="/log-in" className='user disappear'>Log In</Link>
                <Link to="/sign-up" className='user disappear'>Sign Up</Link>
                <button id='btn-menu' onClick={changeDisplayMenu} ><span className="material-symbols-outlined menu">menu</span></button>
                <div id='overMenu' style={{"display": isMenuDisplayed}} >
                    <Link to="/log-in" className='user link-menu'>Log In</Link>
                    <Link to="/sign-up" className='user link-menu'>Sign Up</Link>
                </div>
            </div>
        </nav>
    )
}

export default Navbar;
