import React, { useState } from 'react';
import { Link, Outlet } from "react-router-dom";
import { BrowserRouter, Routes, Route, } from "react-router-dom";
import './gamesPage.css';

import Navbar from './navbar';
import Footer from './footer';
import FilteringAndSorting from './filteringAndSorting';
import Main from './main';

const GamesPage = () => {

    const [videogames, setVideogames] = useState([]);

    const [sorting, setSorting] = useState(null);

    const onChangeSorting = event => {
        let sorting = event.target.value;
        let videogamesCopy = [...videogames]
        if (sorting === "A-Z") videogamesCopy.sort((a, b) => a.title.toUpperCase().localeCompare(b.title.toUpperCase()));
        else if (sorting === "Z-A") videogamesCopy.sort((a, b) => b.title.toUpperCase().localeCompare(a.title.toUpperCase()));
        else if (sorting === "asc") videogamesCopy.sort((a, b) => a.basePrice - b.basePrice);
        else if (sorting === "desc") videogamesCopy.sort((a, b) => b.basePrice - a.basePrice);
        else if (sorting === "oldest") videogamesCopy.sort((a, b) => a.releaseDate.replace(/-/g, "") - b.basePrice);
        setVideogames(videogamesCopy)
    }
    
    return (
        <>
        <Navbar />
        <FilteringAndSorting onChangeSorting={onChangeSorting}/>
        <main>
            <Routes>
                <Route index element={<Main setVideogames={setVideogames} videogames={videogames}/>} />
                <Route path=":query" element={<Main setVideogames={setVideogames} videogames={videogames}/>}/>
            </Routes>
        </main>
        <Footer />
        </>
    )
}

export default GamesPage;
