import React, { useEffect, useState } from 'react';

const Sorting = ({onChangeSorting = () => {}, newSearch = false}) => {

    const [isDropdownBoxDisplayed, setIsDropdownBoxDisplayed] = useState("none");

    const [leftSpaceFromWindow, setLeftSpaceFromWindow] = useState(0);

    const changeDisplayOnClick = e => {
        if (isDropdownBoxDisplayed === "none") {
            setIsDropdownBoxDisplayed("block");
            if (e.target == e.currentTarget) setLeftSpaceFromWindow(e.target.getBoundingClientRect().left);
            else setLeftSpaceFromWindow(e.currentTarget.getBoundingClientRect().left)
        }
        else { // stop the propagation when click on checkbox or number input
            if (e.target == e.currentTarget) setIsDropdownBoxDisplayed("none")
        }
    }

    let sortingList = [ // value - text
        ["A-Z", "A - Z"],
        ["Z-A", "Z - A"],
        ["asc", "PRICE ASC"],
        ["desc", "PRICE DESC"],
        ["oldest", "RELEASE DATE ↑"],
        ["newest", "RELEASE DATE ↓"],
    ]

    return (
        <div className='filter-sorting-item' onClick={changeDisplayOnClick} onMouseLeave={() => setIsDropdownBoxDisplayed("none")}>
            sorting
            <div id='dropdown-sorting' style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}}>
                <div id='dropdown-sorting-inner'>
                    {sortingList.map(el => <SortingField el={el} onChangeSorting={onChangeSorting}/>)}
                </div>
            </div>
        </div>
    )
}

export default Sorting;

const SortingField = ({el, onChangeSorting = () => {}}) => {

    const [isChecked, setIsChecked] = useState(false);

    const onChangeSortingRadio = () => {
        onChangeSorting();
        setIsChecked(!isChecked)
    }

    return (
        <div key={el[0]} className="dropdown-sorting-item">
            <input type="radio" name='sorting' value={el[0]} checked={isChecked} onChange={onChangeSortingRadio}/>
            <p>{el[1]}</p>
        </div>
    )
}
