import React, { useState } from 'react';
import { Link } from "react-router-dom";

import "./navbar.css"; 

const Navbar = () => {

    let search = "block"
    if (window.innerWidth < 600) search = "none"

    let possibleUrl = "";
    if (window.location.href !== "http://localhost:3000/") possibleUrl = window.location.pathname

    let params = useParams();

    const [searchValue, setSearchValue] = useState(params); // input value
    const [url, setUrl] = useState(possibleUrl); // url created by value
    const [isCancelDisplayed, setIsCancelDisplayed] = useState("none"); // x
    const [isSearchDisplayed, setIsSearchDisplayed] = useState(search); // search input
    const [isMenuDisplayed, setIsMenuDisplayed] = useState("none"); // menu button

    const onValueChange = event => {
        setSearchValue(event.target.value);
        if (event.target.value === "") setUrl("");
        else setUrl("/videogames/" + event.target.value);
    }

    const onSearchClick = e => {
        if (searchValue === "") e.preventDefault();
        if (isSearchDisplayed !== "block") {
            setIsSearchDisplayed("block");
            setIsCancelDisplayed("block");
        }
    }
    
    // searchbar + cancel
    const fixSearchBar = () => {
        setIsSearchDisplayed("none");
        setIsCancelDisplayed("none");
    }

    const fixSearch = () => {
        if (window.innerWidth <= 600) fixSearchBar();
        else setIsSearchDisplayed("block");
    }

    window.addEventListener("resize", fixSearch);

    // menu
    const changeDisplayMenu = () => {
        if (isMenuDisplayed === "none") showMenu();
        else hideMenu();
    }

    const showMenu = () => setIsMenuDisplayed("block");
    const hideMenu = () => setIsMenuDisplayed("none");

    const fixMenu = () => {
        if (window.innerWidth > 768) hideMenu();
    }

    window.addEventListener("resize", fixMenu);

    return (
        <nav>
            <div>
                <Link to="/">
                    <span className="material-symbols-outlined home">home</span>
                </Link>
                <input type="text" className="search" placeholder='search...' value={searchValue} onChange={onValueChange} style={{"display": isSearchDisplayed}}></input>
                <button id='btn-cancel' onClick={fixSearchBar} style={{"display": isCancelDisplayed}}><span className="material-symbols-outlined cancel">close</span></button>
                <Link to={url + "?historicalLow=false"} onClick={onSearchClick}><button id="btn-lens"><span className="material-symbols-outlined lens">search</span></button></Link>
            </div>
            <div>
                <Link to="/log-in" className='user disappear'>Log In</Link>
                <Link to="/sign-up" className='user disappear'>Sign Up</Link>
                <button id='btn-menu' onClick={changeDisplayMenu}><span className="material-symbols-outlined menu">menu</span></button>
                <div id='overMenu' style={{"display": isMenuDisplayed}} >
                    <Link to="/log-in" className='user link-menu'>Log In</Link>
                    <Link to="/sign-up" className='user link-menu'>Sign Up</Link>
                </div>
            </div>
        </nav>
    )
}

export default Navbar;
