import React, { useEffect, useState } from 'react';

import './filteringAndSorting.css';

import FilterItem from './filterItem';
import Sorting from './sorting';


const FilteringAndSorting = ({onChangeSorting = () => {}, setSearchParams = () => {}, searchParams = {}}) => {

    //window.location.href = "http://localhost:8080/videogame/33"

    // const horizontalScroll = e => {
    //     const delta = Math.max(-1, Math.min(1, (e.nativeEvent.wheelDelta || -e.nativeEvent.detail)))
    //     e.currentTarget.scrollLeft -= (delta * 100)
    // }

    const [types, setTypes] = useState([]);
    const [categories, setCategories] = useState([]);
    const [systems, setSystems] = useState([]);

    const addOrRemoveField = (filter, field) => {
        let fieldsCopy;
        if (filter === "types") {
            fieldsCopy = [...types];
            types.includes(field) ? fieldsCopy = fieldsCopy.filter(n => n != field) : fieldsCopy.push(field);
            setTypes(fieldsCopy);
        } else if (filter === "categories") {
            fieldsCopy = [...categories];
            categories.includes(field) ? fieldsCopy = fieldsCopy.filter(n => n != field) : fieldsCopy.push(field);
            setCategories(fieldsCopy);
        } else if (filter === "systems") {
            fieldsCopy = [...systems];
            systems.includes(field) ? fieldsCopy = fieldsCopy.filter(n => n != field) : fieldsCopy.push(field);
            setSystems(fieldsCopy);
        }
        changeUrl(filter, fieldsCopy.join())

    }

    const changeUrl = (filter, fieldsCopy) => {
        if (fieldsCopy.length == 0) {
            const {[filter]: deleted, ...newSearchParam} = Object.fromEntries([...searchParams]);
            setSearchParams(newSearchParam);
        }
        else setSearchParams({ ...Object.fromEntries([...searchParams]), [filter]: fieldsCopy});
    }


    return (
        <div id='filter-sorting-container' /*onWheel={horizontalScroll}*/>

            <div id='filter-sorting-bar'>

                    <FilterItem text="types" addOrRemoveField={addOrRemoveField}/>
                    <FilterItem text="categories" addOrRemoveField={addOrRemoveField}/>
                    <FilterItem text="price" />
                    <FilterItem text="discount" />
                    <FilterItem text="systems" addOrRemoveField={addOrRemoveField}/>
                    <FilterItem text="release year" />

                    {/* historical low don't need a dropdown */}
                    <div className='filter-sorting-item' style={{whiteSpace: "nowrap"}}>
                        historical low
                   </div>

                   <Sorting onChangeSorting={onChangeSorting}/>

            </div>

        </div>
    )

}

export default FilteringAndSorting;
