import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, } from "react-router-dom";
import './App.css';

import HomePage from './homePage';
import GamesPage from './gamesPage';

const App = () => {

  const [searchValue, setSearchValue] = useState("");

  const [videogames, setVideogames] = useState([]);

  const onUrlChange = event => {
    setSearchValue(event.target.value);
  }

  const onSearchClick = () => {
    if (searchValue === "") return;
    fetch("http://localhost:8081/videogame/" + searchValue)
    .then(response => response.json())
    .then(response => setVideogames(response))
  }

  return (
    <BrowserRouter>
      <Routes>
          <Route path="/" element={<HomePage onUrlChange={onUrlChange} onSearchClick={onSearchClick} searchValue={searchValue}/>} />
          <Route path="/videogames" element={<GamesPage onUrlChange={onUrlChange} onSearchClick={onSearchClick} searchValue={searchValue}/>} />
          <Route path='/log-in' element={<div>log in</div>} />
          <Route path='sign-up' element={<div>sign-up</div>} />
        <Route path="*" element={<div>wrong url</div>} />
      </Routes>
      {/*img.forEach(n => n)/*img.forEach(n => <img src={"data:image/png;base64," + n.cover} />) */}
    </BrowserRouter>
  )
}

export default App;
