import React, { useEffect, useState } from 'react';

const DropDownBoxRange = ({isDropdownBoxDisplayed = "none", leftSpaceFromWindow, text = ""}) => {

    let min = "0";
    let max;
    let minPlaceholder;
    let maxPlaceholder;

    if (text === "price") {
        max = "999";
        minPlaceholder = min + " €"
        maxPlaceholder = max + " €"
    }
    else if (text === "discount") {
        max = "100";
        minPlaceholder = min + "%"
        maxPlaceholder = max + "%"
    }
    else if (text === "release year") {
        min = "2000";
        max = new Date().getFullYear().toString();
        minPlaceholder = min;
        maxPlaceholder = max;
    }
    
    return (
        <div style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}} className="dropdown-box-range" >
            <div className="dropdown-box-range-inner">
                <p>from</p>
                <input type="number" min={min} max={max} placeholder={minPlaceholder} value=""/>
                <p>to</p>
                <input type="number" min={min} max={max} placeholder={maxPlaceholder} value=""/>
                <p><button>go</button></p>
            </div>
        </div>
    )
}

export default DropDownBoxRange;
