import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Footer from "./footer";
import Navbar from "./navbar";

import './singleGame.css'

const SingleGame = () => {

    let param = useParams();

    const [game, setGame] = useState({});
    const [categories, setCategories] = useState([])

    useEffect(
        () => {
            fetch("http://localhost:8081/videogame/" + param.id)
            .then(response => response.json())
            .then(responseJSON => {
                setGame(responseJSON);
                let boh = "";
                for (const category of responseJSON.categories) {
                    boh += category.category + ", "
                }
                setCategories(boh)
            })
        }, []
    )
    
    return (
        <>
          <Navbar />
            <div id="game-container">
                <div id="game-information-container">
                    <div id="game-information">
                        <p id="game-title">{game.title}</p>
                        <div>
                            <p>type: {game.type}</p>
                            <p>developer: {game.developer}</p>
                            <p>publisher: {game.publisher}</p>
                            <p>release date: {game.releaseDate}</p>
                            <p>categories: {categories}</p>
                        </div>
                    </div>
                    <div id="img-container">
                        <img src={"data:image/png;base64," + game.cover} id="game-img" />
                    </div>
                </div>
            </div>
            <Footer />
        </>
    )

}

export default SingleGame;
