import { useState } from "react";
import { Link } from "react-router-dom";

import './userPage.css'

const UserPage = ({user = false, setUser = () => {}}) => {

    const [userEmail, setUserEmail] = useState("");
    const [userPassword, setUserPassword] = useState("");

    const [isModelDisplayed, setIsModelDisplayed] = useState("none")

    const emailOnChange = e => setUserEmail(e.target.value);

    const passwordOnchange = e => setUserPassword(e.target.value)

    const addUserOnClick = e => {
        e.preventDefault();
        fetch("http://localhost:8081/user", {
            method: 'POST',
            body: JSON.stringify({
                email: userEmail,
                password: userPassword
            }),
            mode: 'cors',
            headers: {
            'Access-Control-Allow-Origin':'*',
            'Content-Type': 'application/json'
            }
        })
        .then(response => response.json())
        .then(responseJSON => {
            if (responseJSON) {
                setUser(true);
                window.location = "/"
            }
            else {
                setIsModelDisplayed("block");
                setTimeout(() => setIsModelDisplayed("none"), 2000)
            }
        })
    }

    return (
        <div>
            <div className='container-title'>
                    <span className="material-symbols-outlined logo">bar_chart</span>
                    <h1 className='title'>VIDEOGAMES PRICE TRACKER</h1>
            </div>
            <div id="userPage-container">
                <fieldset>
                    <legend>userPage</legend>
                    <div className="userPage-field-container">
                        <label htmlFor="user-email">email</label>
                        <input type="email" id="user-email" value={userEmail} onChange={emailOnChange} autoComplete="true"/>
                    </div>
                    <div className="userPage-field-container">
                        <label htmlFor="user-password">password</label>
                        <input type="password" id="user-password" value={userPassword} onChange={passwordOnchange}/>
                    </div>
                    <div id="userPage-a-container">
                        <Link to="/" onClick={addUserOnClick}>Sign-In</Link>
                    </div>
                </fieldset>
            </div>
            <div id="userPage-modal" style={{display: isModelDisplayed}}>
                <p>email already exists</p>
            </div>
        </div>
    )
}

export default UserPage;
