import React, { useEffect, useState } from 'react';

const Sorting = ({onChangeSorting = () => {}, newSearch = false}) => {

    const [isDropdownBoxDisplayed, setIsDropdownBoxDisplayed] = useState("none");

    const [leftSpaceFromWindow, setLeftSpaceFromWindow] = useState(0);

    const [currentSorting, setCurrentSorting] = useState([false, false, false, false, false, false]);

    const changeDisplayOnClick = e => {
        if (isDropdownBoxDisplayed === "none") {
            setIsDropdownBoxDisplayed("block");
            if (e.target == e.currentTarget) setLeftSpaceFromWindow(e.target.getBoundingClientRect().left);
            else setLeftSpaceFromWindow(e.currentTarget.getBoundingClientRect().left)
        }
        else { // stop the propagation when click on checkbox or number input
            if (e.target == e.currentTarget) setIsDropdownBoxDisplayed("none")
        }
    }

    let sortingList = [ // value - text
        ["A-Z", "A - Z"],
        ["Z-A", "Z - A"],
        ["asc", "PRICE ASC"],
        ["desc", "PRICE DESC"],
        ["oldest", "RELEASE DATE ↑"],
        ["newest", "RELEASE DATE ↓"],
    ]

    if (newSearch) {
        console.log("passato");
        let currentSortingCopy = [];
        let bool = false;
        for (const c of currentSorting) {
            if (c == true) bool = true;
            currentSortingCopy.push(false)
        }
        if (bool) setCurrentSorting(currentSortingCopy)
    }

    const onChangeSortingClick = (e) => {
        onChangeSorting(e);
        let currentSortingCopy = [...currentSorting]
        for (let i = 0; i < currentSortingCopy.length; i++) {
            if (i == e.key) currentSortingCopy[i] = true;
            else currentSortingCopy[i] = false;
        }

        setCurrentSorting(currentSortingCopy)
    }

    return (
        <div className='filter-sorting-item' onClick={changeDisplayOnClick} onMouseLeave={() => setIsDropdownBoxDisplayed("none")}>
            sorting
            <div id='dropdown-sorting' style={{display: isDropdownBoxDisplayed, left: leftSpaceFromWindow}}>
                <div id='dropdown-sorting-inner'>
                {sortingList.forEach((e, index) => console.log(currentSorting[index]))}
                    {sortingList.map((el, index) => <div key={index} className="dropdown-sorting-item">
                        <input type="radio" name='sorting' value={el[0]} checked={currentSorting[index]} onChange={onChangeSortingClick}/>
                        <p>{el[1]}</p>
                    </div>)}
                </div>
            </div>
        </div>
    )
}

export default Sorting;
